class CnDefinitionTests < Test::Unit::TestCase  # :nodoc:

  def test_cn
    assert_equal "New Year's Day", (Holidays.on(Date.civil(2016, 1, 1), [:cn], [:observed])[0] || {})[:name]

    assert_equal "Lunar New Year's Day", (Holidays.on(Date.civil(2016, 2, 8), [:cn], [:observed])[0] || {})[:name]

    assert_equal "The second day of Lunar New Year", (Holidays.on(Date.civil(2016, 2, 9), [:cn], [:observed])[0] || {})[:name]

    assert_equal "The third day of Lunar New Year", (Holidays.on(Date.civil(2016, 2, 10), [:cn], [:observed])[0] || {})[:name]

    assert_equal "Labour Day", (Holidays.on(Date.civil(2016, 5, 2), [:cn], [:observed])[0] || {})[:name]

    assert_equal "Mid-Autumn Festival", (Holidays.on(Date.civil(2016, 9, 15), [:cn], [:observed])[0] || {})[:name]

    assert_equal "National Day", (Holidays.on(Date.civil(2016, 10, 1), [:cn], [:observed])[0] || {})[:name]

    assert_equal "New Year's Day", (Holidays.on(Date.civil(2017, 1, 2), [:cn], [:observed])[0] || {})[:name]

    assert_equal "Lunar New Year's Day", (Holidays.on(Date.civil(2017, 1, 28), [:cn], [:observed])[0] || {})[:name]

    assert_equal "The second day of Lunar New Year", (Holidays.on(Date.civil(2017, 1, 30), [:cn], [:observed])[0] || {})[:name]

    assert_equal "The third day of Lunar New Year", (Holidays.on(Date.civil(2017, 1, 31), [:cn], [:observed])[0] || {})[:name]

    assert_equal "Labour Day", (Holidays.on(Date.civil(2017, 5, 1), [:cn], [:observed])[0] || {})[:name]

    assert_equal "Mid-Autumn Festival", (Holidays.on(Date.civil(2017, 9, 15), [:cn], [:observed])[0] || {})[:name]

    assert_equal "National Day", (Holidays.on(Date.civil(2017, 10, 2), [:cn], [:observed])[0] || {})[:name]

  end
